/**
 * @file LogicFlow相关工具函数
 */
import LogicFlow, { RectNode, RectNodeModel } from "@logicflow/core";
export * from "@logicflow/core";
import type { Definition, GraphConfigData, NodeData, RegisterConfig } from "@logicflow/core";
import {
  Menu,
  RectResize,
  SelectionSelect,
  Control,
  ContextMenu,
  DndPanel
} from "@logicflow/extension";
import {testNodeType} from "../custom-node/";

/** 获取配置 */
export const getLogicFlowOptions = (containerEl: HTMLElement): Definition => {
  const options: Definition = {
    container: containerEl,
    // grid: true,
    // 引入插件
    plugins: [Menu, ContextMenu, SelectionSelect, Control, DndPanel],
    keyboard: {
      enabled: true,
    },
    grid: {
      visible: false,
      size: 5,
    },
    background: {
      backgroundImage:
        'url("data:image/svg+xml;base64,")',
      backgroundRepeat: "repeat",
    },
  };
  return options;
};
