/** 初始化LogicFlow插件相关，必须在 lf.render() 之前设置） */
export const initLogicFlowPlugins = (lf: LogicFlow): void => {
  // 为菜单追加选项（必须在 lf.render() 之前设置）
  // Menu
  const menu = lf.extension.menu as Menu;
  if (menu) {
    menu.addMenuConfig({
      nodeMenu: [
        {
          text: "分享",
          callback() {
            alert("分享成功！");
          },
        },
        {
          text: "属性",
          callback(node: NodeData) {
            alert(`
                节点id：${node.id}
                节点类型：${node.type}
                节点坐标：(x: ${node.x}, y: ${node.y})`);
          },
        },
      ],
      edgeMenu: [
        {
          text: "属性",
          callback(edge: NodeData) {
            alert(`
                边id：${edge.id}
                边类型：${edge.type}
                边坐标：(x: ${edge.x}, y: ${edge.y})
                源节点id：${edge.sourceNodeId}
                目标节点id：${edge.targetNodeId}`);
          },
        },
      ],
      graphMenu: [
        {
          text: "分享",
          callback() {
            alert("分享成功！");
          },
        },
      ],
    });

    if (typeof lf.setMenuByType === "function") {
      lf.setMenuByType({
        type: "lf:defaultSelectionMenu",
        menu: [],
      });
    }
  }

  // ContextMenu
  if (lf.setContextMenuItems) {
    const commonMenuConfig = {
      icon: "data:image/png;base64,",
      callback: (data: NodeData) => {
        lf.deleteElement(data.id);
        lf.hideContextMenu();
      },
    };
    lf.setContextMenuItems(commonMenuConfig);
  }

  // SelectionSelect
  // Control
  // DndPanel
  const dndPanel = lf.extension.dndPanel as DndPanel;
  if (dndPanel) {
    dndPanel.setPatternItems([
      {
        label: "选区",
        icon: "data:image/png;base64,",
        callback: () => {
          lf.extension.selectionSelect.openSelectionSelect();
          lf.once("selection:selected", () => {
            lf.extension.selectionSelect.closeSelectionSelect();
          });
        },
      },
      {
        type: "circle",
        text: "开始",
        label: "开始节点",
        icon: "data:image/png;base64,",
      },
      {
        type: "rect",
        label: "用户任务",
        icon: "data:image/png;base64,",
        className: "important-node",
      },
      {
        type: "rect",
        label: "系统任务",
        icon: "data:image/png;base64,",
        className: "import_icon",
      },
      {
        type: "diamond",
        label: "条件判断",
        icon: "data:image/png;base64,",
      },
      {
        type: "circle",
        text: "结束",
        label: "结束节点",
        icon: "data:image/png;base64,",
      },
    ]);
  }
};

/** 注册自定义节点、边等 */
export const registerLogicFlowCustoms = (lf: LogicFlow): void => {
  lf.register(testNodeType);
}

/** 初始化LogicFlow流程设计器 */
export const setupLogicFlow = (containerEl: HTMLElement): LogicFlow => {
  /** LogicFlow参数 */
  const options = getLogicFlowOptions(containerEl);
  /** LogicFlow渲染数据 */
  const graphData = getLogicFlowData();

  // 初始化LogicFlow
  const lf = new LogicFlow(options);
  // 初始化相关插件
  initLogicFlowPlugins(lf);
  // 注册自定义节点、边
  registerLogicFlowCustoms(lf);
  // 渲染图
  lf.render(graphData);
  // 图形画布居中显示
  lf.translateCenter();
  return lf
};